import * as React from "react";
import { Autocomplete } from "@base-ui-components/react/autocomplete";

export default function ExampleFlagSelector() {
  const [value, setValue] = React.useState<string>("");

  return (
    <div className="mx-auto w-[20rem]">
      <Autocomplete.Root
        items={countries}
        value={value}
        onValueChange={(val, details) => {
          if (details.reason === "item-press") {
            setValue(val ?? "");
          }
        }}
      >
        <Autocomplete.Trigger
          className="h-10 w-full rounded-md border border-gray-200 bg-[canvas] px-3.5 text-left text-base text-gray-900 outline-none hover:bg-gray-100 focus-visible:outline focus-visible:outline-2 focus-visible:-outline-offset-1 focus-visible:outline-blue-800"
          aria-label="Choose a country"
        >
          {value || "Select a countryâ€¦"}
        </Autocomplete.Trigger>

        <Autocomplete.Portal>
          <Autocomplete.Positioner sideOffset={4} align="start">
            <Autocomplete.Popup className="max-h-80 w-[20rem] overflow-auto rounded-lg border border-gray-200 bg-[canvas] text-gray-900 shadow-lg shadow-gray-200">
              <div className="p-2">
                <Autocomplete.Input
                  placeholder="Search countriesâ€¦"
                  className="h-9 w-full rounded-md border border-gray-200 px-3 text-base text-gray-900 focus:outline focus:outline-2 focus:-outline-offset-1 focus:outline-blue-800"
                />
              </div>
              <Autocomplete.Empty className="px-4 py-2 text-sm text-gray-600">
                No countries found
              </Autocomplete.Empty>
              <Autocomplete.List className="grid grid-cols-3 gap-1 p-2">
                {(country: Country) => (
                  <Autocomplete.Item
                    key={country.id}
                    value={country.name}
                    className="flex cursor-pointer flex-col items-center justify-center gap-1 rounded-md px-2 py-2 text-sm hover:bg-gray-100 data-[highlighted]:bg-blue-600 data-[highlighted]:text-white"
                  >
                    <span className="text-2xl">{country.flag}</span>
                    <span className="truncate">{country.name}</span>
                  </Autocomplete.Item>
                )}
              </Autocomplete.List>
            </Autocomplete.Popup>
          </Autocomplete.Positioner>
        </Autocomplete.Portal>
      </Autocomplete.Root>
    </div>
  );
}

interface Country {
  id: string;
  name: string;
  region: string;
  flag: string;
}

