import React from "react";
import Link from "next/link.js";

import { cn } from "@/lib/utils";
import {
  NavigationMenu,
  NavigationMenuContent,
  NavigationMenuIcon,
  NavigationMenuItem,
  NavigationMenuLink,
  NavigationMenuList,
  NavigationMenuTrigger,
  navigationMenuTriggerStyle,
  NavigationSubMenuTrigger,
} from "@/registry/default/ui/navigation-menu";

const products = [
  {
    title: "Enterprise",
    href: "/products/enterprise",
    description: "Complete solution for large organizations and enterprises.",
  },
  {
    title: "Professional",
    href: "/products/professional",
    description: "Advanced features for growing businesses.",
  },
  {
    title: "Starter",
    href: "/products/starter",
    description: "Perfect for individuals and small teams.",
  },
  {
    title: "Free",
    href: "/products/free",
    description: "Basic features to get started.",
  },
];

const services = [
  {
    title: "Consulting",
    href: "/services/consulting",
    description: "Expert guidance and strategic planning.",
  },
  {
    title: "Training",
    href: "/services/training",
    description: "Comprehensive training programs for your team.",
  },
  {
    title: "Support",
    href: "/services/support",
    description: "24/7 technical support and assistance.",
  },
  {
    title: "Implementation",
    href: "/services/implementation",
    description: "Professional setup and configuration services.",
  },
];

const resources = [
  {
    title: "Documentation",
    href: "/resources/docs",
    description: "Detailed guides and API references.",
  },
  {
    title: "Blog",
    href: "/resources/blog",
    description: "Latest news, updates, and insights.",
  },
  {
    title: "Community",
    href: "/resources/community",
    description: "Join our growing community of users.",
  },
  {
    title: "Case Studies",
    href: "/resources/case-studies",
    description: "Success stories from our customers.",
  },
];

export default function NavigationMenuNested() {
  return (
    <NavigationMenu>
      <NavigationMenuList>
        <NavigationMenuItem>
          <NavigationMenuTrigger>Products</NavigationMenuTrigger>
          <NavigationMenuContent>
            <ul className="grid gap-2 md:w-[400px] lg:w-[500px] lg:grid-cols-[.75fr_1fr]">
              <li className="row-span-3">
                <NavigationMenuLink
                  render={
                    <Link
                      className="from-muted/50 to-muted flex h-full w-full flex-col justify-end rounded-md bg-linear-to-b p-6 no-underline outline-hidden select-none focus:shadow-md"
                      href="/products"
                    >
                      <div className="mt-4 mb-2 text-lg font-medium">
                        Our Products
                      </div>
                      <p className="text-muted-foreground text-sm leading-tight">
                        Discover our range of solutions for your business needs.
                      </p>
                    </Link>
                  }
                />
              </li>
              {products.slice(0, 2).map((product) => (
                <ListItem
                  key={product.title}
                  title={product.title}
                  href={product.href}
                >
                  {product.description}
                </ListItem>
              ))}
              <NavigationMenu
                contentProps={{
                  side: "right",
                  align: "end",
                  sideOffset: 20,
                  alignOffset: -17,
                }}
              >
                <NavigationMenuItem>
                  <NavigationSubMenuTrigger
                    render={(props, state) => (
                      <ListItem
                        href="/products/enterprise"
                        title="Enterprise Solutions"
                        {...props}
                      >
                        Advanced features for large organizations
                        <NavigationMenuIcon
                          className={cn("-rotate-90", {
                            "rotate-90": state.open,
                          })}
                        />
                      </ListItem>
                    )}
                  />
                  <NavigationMenuContent>
                    <ul className="flex w-[300px] flex-col gap-2">
                      {products.slice(2).map((product) => (
                        <ListItem
                          key={product.title}
                          title={product.title}
                          href={product.href}
                        >
                          {product.description}
                        </ListItem>
                      ))}
                    </ul>
                  </NavigationMenuContent>
                </NavigationMenuItem>
              </NavigationMenu>
            </ul>
          </NavigationMenuContent>
        </NavigationMenuItem>
        <NavigationMenuItem>
          <NavigationMenuTrigger>Services</NavigationMenuTrigger>
          <NavigationMenuContent>
            <ul className="grid gap-2 md:w-[400px] lg:w-[500px] lg:grid-cols-[.75fr_1fr]">
              <li className="row-span-3">
                <NavigationMenuLink
                  render={
                    <Link
                      className="from-muted/50 to-muted flex h-full w-full flex-col justify-end rounded-md bg-linear-to-b p-6 no-underline outline-hidden select-none focus:shadow-md"
                      href="/services"
                    >
                      <div className="mt-4 mb-2 text-lg font-medium">
                        Our Services
                      </div>
                      <p className="text-muted-foreground text-sm leading-tight">
                        Professional services to help your business grow.
                      </p>
                    </Link>
                  }
                />
              </li>
              {services.slice(0, 2).map((service) => (
                <ListItem
                  key={service.title}
                  title={service.title}
                  href={service.href}
                >
                  {service.description}
                </ListItem>
              ))}
              <NavigationMenu
                contentProps={{
                  side: "right",
                  align: "end",
                  sideOffset: 20,
                  alignOffset: -17,
                }}
              >
                <NavigationMenuItem>
                  <NavigationSubMenuTrigger
                    render={(props, state) => (
                      <ListItem
                        href="/services/support"
                        title="Support Services"
                        {...props}
                      >
                        Comprehensive support options
                        <NavigationMenuIcon
                          className={cn("-rotate-90", {
                            "rotate-90": state.open,
                          })}
                        />
                      </ListItem>
                    )}
                  />
                  <NavigationMenuContent>
                    <ul className="flex w-[300px] flex-col gap-2">
                      {services.slice(2).map((service) => (
                        <ListItem
                          key={service.title}
                          title={service.title}
                          href={service.href}
                        >
                          {service.description}
                        </ListItem>
                      ))}
                    </ul>
                  </NavigationMenuContent>
                </NavigationMenuItem>
              </NavigationMenu>
            </ul>
          </NavigationMenuContent>
        </NavigationMenuItem>
        <NavigationMenuItem>
          <NavigationMenuTrigger>Resources</NavigationMenuTrigger>
          <NavigationMenuContent>
            <ul className="grid w-[400px] gap-2 md:w-[500px] md:grid-cols-2 lg:w-[600px]">
              {resources.map((resource) => (
                <ListItem
                  key={resource.title}
                  title={resource.title}
                  href={resource.href}
                >
                  {resource.description}
                </ListItem>
              ))}
            </ul>
          </NavigationMenuContent>
        </NavigationMenuItem>
        <NavigationMenuItem>
          <NavigationMenuLink
            render={
              <Link className={navigationMenuTriggerStyle()} href="/contact">
                Contact
              </Link>
            }
          />
        </NavigationMenuItem>
      </NavigationMenuList>
    </NavigationMenu>
  );
}

function ListItem({
  title,
  children,
  href,
  ...props
}: React.ComponentPropsWithoutRef<"li"> & { href: string }) {
  return (
    <li {...props}>
      <NavigationMenuLink
        render={
          <Link href={href} className="flex flex-col gap-2 no-underline">
            <div className="text-sm leading-none font-medium">{title}</div>
            <p className="text-muted-foreground line-clamp-2 flex text-sm leading-snug">
              {children}
            </p>
          </Link>
        }
      />
    </li>
  );
}
