import { Separator } from "@/registry/default/ui/separator";

export default function SeparatorVertical() {
  return (
    <div className="p-4">
      <div className="flex h-10 items-center">
        <span>Home</span>
        <Separator orientation="vertical" className="mx-2 h-6" />
        <span>Products</span>
        <Separator orientation="vertical" className="mx-2 h-6" />
        <span>About</span>
        <Separator orientation="vertical" className="mx-2 h-6" />
        <span>Contact</span>
      </div>
    </div>
  );
}
