import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "@/registry/default/ui/tooltip";

import { Button } from "../ui/button";

export default function TooltipDemo() {
  return (
    <TooltipProvider>
      <Tooltip>
        <TooltipTrigger render={<Button>Hover</Button>} />
      </Tooltip>
      <TooltipContent>asdasd</TooltipContent>
    </TooltipProvider>
  );
}
